      key: "actualizacion",
    },
    {
      title: "Accci贸n",
      dataIndex: "",
      key: "x",
      render: (fila) => (
        <Tooltip title="Eliminar">
          <Button
            type="danger"
            shape="circle"
            onClick={() => seleccionarTicket(fila)}
