    <div className="site-card-border-less-wrapper">
      <Card
        title="Tickets"
        bordered={false}
        style={{ width: FullscreenExitOutlined }}
      >
        {/* <Table
          // style={{ marginTop: "2rem" }}
          // columns={columns}
          // dataSource={dataSource}
          // rowSelection={rowSelection}
        /> */}
      </Card>
    </div>
  );
};

export default Tickets;
