import React from "react";
import { Table, Tooltip, Button } from "antd";
import { DeleteOutlined } from '@ant-design/icons';


//REDUX
import { useDispatch } from "react-redux";
import {borrarDocumentoAction} from '../actions/documentosActions';


const tablaDocumentos = ({ documentos }) => {

  const dispatch = useDispatch();
//confirmar si desea eliminar
const confirmarEliminarDocumento = id => {

  //preguntar al usuario

  //pasar al action
  dispatch(borrarDocumentoAction(id));

}


  const columns = [
    { title: "Factura", dataIndex: "factura", key: "factura" },
    { title: "Fecha", dataIndex: "fecha", key: "fecha" },
    { title: "Valor", dataIndex: "valor", key: "valor" },
    { title: "Admisi贸n", dataIndex: "admision", key: "admision" },
    { title: "Apb", dataIndex: "apb", key: "apb" },
    {
      title: "Accci贸n",
      dataIndex: "",
      key: "x",
      render: () => <Tooltip title="search">
      <Button type="primary" shape="circle" onClick={() => confirmarEliminarDocumento(id)} icon={<DeleteOutlined />} />
    </Tooltip>,
    },
  ];

  const data = [
    {
      key: 1,
      factura: 1802526,
      fecha: "06-11-2020",
      valor: 1250000,
      admision: 895241,
      apb: "Coomeva EPS",
    },
    {
      key: 1,
      factura: 1802526,
      fecha: "06-11-2020",
      valor: 1250000,
      admision: 895241,
      apb: "Coomeva EPS",
    },
  ];
  let dataNew;
  if (!documentos.length) {
    dataNew = [];
  } else {
    documentos.map((documento) => {
      dataNew = [
        {
          key: documento.Numero,
          factura: documento.CONS_DOC_CONT,
          fecha: documento.Fecha,
          valor: documento.Valor,
          admision: documento.ID_ADMIS_ADXFAC,
          apb: documento.APB,
        },
      ];
    });
  }

  return <Table columns={columns} dataSource={dataNew} />;
};

export default tablaDocumentos;
