import React from "react";
import { Card, Input } from "antd";
import {useDispatch, useSelector} from 'react-redux' //para acceder al store

//ACTIONS DE REDUX
import { traerDocumentospActios } from "../actions/documentosActions";

import { FullscreenExitOutlined, AudioOutlined } from "@ant-design/icons";
import TablaDocumentos from "./TablaDocumentos";


const Documentos = () => {

  //utilizar use dispatch y te crea una funcion
  const dispatch = useDispatch();

  const traerDocumento = () => dispatch(traerDocumentospActios());

//cuano el usuario haga submit
const submitTraerDocumento = e => {
  e.preventDefault();

  //validar formulario

  //si no hay errores

  //mostrar producto
  traerDocumento()
}
//fin


  const { Search } = Input;
  const onSearch = (value) => console.log(value);

  //#region DATOS PRUEBAS
  const dataSource = [
    {
      key: "1",
      name: "Mike",
      age: 32,
      address: "10 Downing Street",
    },
    {
      key: "2",
      name: "John",
      age: 42,
      address: "10 Downing Street",
    },
  ];

  const columns = [
    {
      title: "Name",
      dataIndex: "name",
      key: "name",
    },
    {
      title: "Age",
      dataIndex: "age",
      key: "age",
    },
    {
      title: "Address",
      dataIndex: "address",
      key: "address",
    },
  ];

  //#endregion FIN DATOS PRUEBA
  
  return (
    <div className="site-card-border-less-wrapper">
      <Card
        title="Documentos"
        bordered={false}
        style={{ width: FullscreenExitOutlined }}
      >
        <form action="">
          <Search
            placeholder="Ingresar texto a buscar"
            onSearch={onSearch}
            style={{ width: 200 }}
          />
        </form>
      </Card>
      <TablaDocumentos/>
      {/* <Table dataSource={dataSource} columns={columns} />; */}
    </div>
  );
};

export default Documentos;
