//IMPORTANDO TYPES
import {
  TRAER_DOCUMENTO,
  TRAER_DOCUMENTO_EXITO,
  TRAER_DOCUMENTO_ERROR,
} from "../types";
import clienteAxios from '../config/axios.js'

//TRAER LOS DOCUMENTOS
//recibimos el tipo de documento y el numero de documento desde componente documento
export function traerDocumentosActions(documento) {
  return (dispatch) => {
      dispatch (traerDocumento())
        try {
            //consulta en la base de datos
         const res =   clienteAxios.get(`/factura/${documento}`)
            dispatch (traerDocumentoExito(documento));
        } catch (error) {
            dispatch (traerDocumentoError(true));
        }
  }
}


//FUNCIONES - ACCIONES

const traerDocumento = (documento) =>({
    type: TRAER_DOCUMENTO,
    payload: true
})

//si se traer el documento exitosamente del api
const traerDocumentoExito = (documento) =>({
    type: TRAER_DOCUMENTO_EXITO,
    payload: documento
})

//si se presenta un error

const traerDocumentoError = (estado) =>({
    type: TRAER_DOCUMENTO_ERROR,
    payload: estado
})
