import React from "react";
import counter, {
  actionCreators as counterActions,
  selector as counterSelector,
} from "../features/counter";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";

import { Row, Col, Typography, Card, Button } from "antd";
import { MinusOutlined, PlusOutlined } from "@ant-design/icons";

const Counter = ({ counter, counterActions }) => {
  return (
    <React.Fragment>
      <Card title="Actividad de Contador" bordered={false}>
        <Row>
          <Col md={9} />
          <Col md={2}>
            <Button
              size="large"
              icon={<MinusOutlined />}
              shape="circle"
              onClick={counterActions.increment}
            />
          </Col>
          <Col md={2}>
            <span style={{ fontSize: "2rem", fontWeight: "bold" }}>
              {counter}
            </span>
          </Col>
          <Col md={2}>
            <Button
              size="large"
              icon={<PlusOutlined />}
              shape="circle"
              onClick={increment}
            />
          </Col>
          <Col md={9} />
        </Row>
      </Card>
    </React.Fragment>
  );
};

//conectar store
const mapStateToProps = (state) => ({ ...counterSelector(state) });

const mapDispatchToProps = (dispatch) => ({
  counterActions: bindActionCreators(counterActions, dispatch),
});
export default connect(mapStateToProps, mapDispatchToProps)(Counter);
