import React, { useState } from "react";
import { Card, Input, Select } from "antd";
import { useDispatch, useSelector } from "react-redux"; //para acceder al store

//ACTIONS DE REDUX
import { traerDocumentosActions } from "../actions/documentosActions";

import { FullscreenExitOutlined, AudioOutlined } from "@ant-design/icons";
import TablaDocumentos from "./TablaDocumentos";

const Documentos = () => {
  const { Option } = Select;
  //state Componente
  const [valor, setValor] = useState(""); //state para guardar valor a buscar
  const [tipoDocumento, setTipoDocumento] = useState("");

  //utilizar use dispatch y te crea una funcion
  const dispatch = useDispatch();

  //mandar a llamar el action de documentoAction
  const traerDocumento = () => dispatch(traerDocumentosActions());

  //cuano el usuario haga submit
  const submitTraerDocumento = (valor, e) => {
    e.preventDefault();
    console.log(valor);
    //validar formulario
    if (valor.trim() === "") {
      return;
    }
    //si no hay errores

    //traer producto
    traerDocumento();
  };

  //funcion para capturar evento de enter en input de busqueda
  const submitTraerDocumentoEnter = (e) => {
    e.preventDefault();

    //validar formulario
    console.log(e);
    //si no hay errores

    //mostrar producto
    traerDocumento();
  };

  //fin

  const { Search } = Input;
  // const onSearch = (value) => console.log(value);

  //#region Tipos de Documentos - SELECT
  // function onChange(value) {
  //   console.log(`selected ${value}`);
  // }
  
  function onBlur() {
    console.log('blur');
  }
  
  function onFocus() {
    console.log('focus');
  }
  
  function onSearch(val) {
    console.log('search:', val);
  }

  //#endregion

  //#region DATOS PRUEBAS
  const dataSource = [
    {
      key: "1",
      name: "Mike",
      age: 32,
      address: "10 Downing Street",
    },
    {
      key: "2",
      name: "John",
      age: 42,
      address: "10 Downing Street",
    },
  ];

  const columns = [
    {
      title: "Name",
      dataIndex: "name",
      key: "name",
    },
    {
      title: "Age",
      dataIndex: "age",
      key: "age",
    },
    {
      title: "Address",
      dataIndex: "address",
      key: "address",
    },
  ];

  //#endregion FIN DATOS PRUEBA

  return (
    <div className="site-card-border-less-wrapper">
      <Card
        title="Documentos"
        bordered={false}
        style={{ width: FullscreenExitOutlined }}
      >
        <form>
          <Select
            showSearch
            style={{ width: 200 }}
            placeholder="Select a person"
            optionFilterProp="children"
            onChange={(e) => setTipoDocumento(e.target.value)}
            onFocus={onFocus}
            onBlur={onBlur}
            onSearch={onSearch}
            filterOption={(input, option) =>
              option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
            }
          >
            <Option value="factura">Factura</Option>
            <Option value="Historia">Historia</Option>
            <Option value="Orden">Orden</Option>
          </Select>
          ,
          <Search
            placeholder="Ingresar texto a buscar"
            onSearch={submitTraerDocumento}
            onPressEnter={submitTraerDocumentoEnter}
            style={{ width: 200 }}
            value={valor}
            onChange={(e) => setValor(Number(e.target.value))}
          />
        </form>
      </Card>
      <TablaDocumentos />
      {/* <Table dataSource={dataSource} columns={columns} />; */}
    </div>
  );
};

export default Documentos;
