import { createStore } from 'redux';


//INITIAL STATE:
const initialState = {
    counter: 10
}


//REDUCER
const operationReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'INCREMENT':
            return {...state, counter: ++state.counter }
        case 'DECREMENT':
            return {...state, counter: --state.counter }
        default:
            return state
    }
}


//STORE
const store = createStore(operationReducer)

export default store;
